import axios from 'axios';
export default {
  props: {
    modelValue: String
  },
  emits: ['update:modelValue'],
  data() {
    return {
      query: '',
      cities: []
    };
  },
  methods: {
    async fetchCities() {
      if (this.query.length < 2) {
        this.cities = [];
        return;
      }
      try {
        const response = await axios.get('/api/database.getCities', {
          params: {
            access_token: process.env.VUE_APP_VK_ACCESS_TOKEN,
            // Токен из .env
            v: process.env.VUE_APP_VK_API_VERSION,
            // Версия API из .env
            q: this.query,
            country_id: 1,
            // Россия
            count: 10
          }
        });
        this.cities = response.data.response.items;
      } catch (error) {
        console.error('Ошибка при получении городов:', error);
      }
    },
    selectCity(city) {
      this.query = city.title;
      this.cities = [];
      this.$emit('update:modelValue', city.id); // Передаем ID выбранного города
    }
  }
};
