import { createStore } from 'vuex';
import axios from 'axios';

export default createStore({
    state: {
        resumes: [],
    },
    mutations: {
        setResumes(state, resumes) {
            state.resumes = resumes;
        },
        updateResumeStatus(state, { id, status }) {
            const resume = state.resumes.find((r) => r._id === id);
            if (resume) {
                resume.status = status;
            }
        },
    },
    actions: {
        async fetchResumes({ commit }) {
            const { data } = await axios.get('http://localhost:3000/api/cv');
            commit('setResumes', data);
        },
        async updateResumeStatus({ commit }, { id, status }) {
            await axios.post(`http://localhost:3000/api/cv/${id}/status/update`, { status });
            commit('updateResumeStatus', { id, status });
        },
    },
    getters: {
        resumesByStatus: (state) => state.resumes,
    },
});
